import { default as React, useEffect, useState } from 'react';
import { COLORS } from '../../assets/data'

import './extra.scss';

const ContactPage = () => {

	const array_lenght = COLORS.length;
	const [ style, setStyle ] = useState({
		color: color,
		fontSize: 24,
	});

	useEffect(({clock}) => {
		const interval = setInterval(() => {
			const random_int = Math.sin(clock.getElapsedTime());
			setStyle({
				color: COLORS[ random_int * array_lenght ],
				fontSize: random_int * 28,
			});
		}, 1000);
		return () => clearInterval(interval);
	}, [])

	return (
		<div className="contact">
			<div className="contact__title">
			<h1>Contact us</h1>
				<p>
					Wanneer je problemen ondervindt bij het opzetten van je RaspberryPi of het RetroPi systeem,
					kan je ons contacteren op dit mailadress
				</p>
			<h2 style={style} id="email" >jeroverv@student.arteveldehs.be</h2>
			</div>

		</div>
	);
};

export default ContactPage;
